using System;\nusing System.IO;\nusing Newtonsoft.Json;\n\npublic class ObjectState\n{\n    public int Id { get; set; }\n    public string State { get; set; }\n\n    // Method to save object state as a JSON file\n    public void Save()\n    {\n        File.WriteAllText($"path\\{Id}.json", JsonConvert.SerializeObject(this));\n    }\n\n    // Method to load object state from a JSON file\n    public static ObjectState Load(int id)\n    {\n        string content = File.ReadAllText($"path\\{id}.json");\n        return JsonConvert.DeserializeObject<ObjectState>(content);\n    }\n}