using Microsoft.Win32;\n\npublic void SetIgnoreSslErrors()\n{\n    // Disable the prompt for untrusted certificates\n    const string userRoot = "HKEY_CURRENT_USER";\n    const string subkey = @"Software\Microsoft\Windows\CurrentVersion\Internet Settings";\n    const string keyName = userRoot + "\\" + subkey;\n\n    Registry.SetValue(keyName, "IE5_RestrictedZone", 0, RegistryValueKind.DWord);\n    Registry.SetValue(keyName, "SecureProtocols", 3072 | 256, RegistryValueKind.DWord); // TLS 1.2 and SSL 3.0\n    Registry.SetValue(keyName, "CertCheckMode", 0, RegistryValueKind.DWord); // Ignore certificate errors\n\n    // Restart the application or Internet Explorer to apply changes\n}