using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class PasswordHasher\n{\n    // Generate a random salt\n    public byte[] GenerateSalt(int length)\n    {\n        using (var randomNumberGenerator = RandomNumberGenerator.Create())\n        {\n            byte[] salt = new byte[length];\n            randomNumberGenerator.GetBytes(salt);\n            return salt;\n        }\n    }\n\n    // Hash the password with the given salt\n    public byte[] ComputeHash(string password, byte[] salt)\n    {\n        using (var sha256 = SHA256.Create())\n        {\n            var combinedBytes = CombineArrays(Encoding.UTF8.GetBytes(password), salt);\n            return sha256.ComputeHash(combinedBytes);\n        }\n    }\n\n    private byte[] CombineArrays(byte[] firstArray, byte[] secondArray)\n    {\n        var bytes = new byte[firstArray.Length + secondArray.Length];\n        Buffer.BlockCopy(firstArray, 0, bytes, 0, firstArray.Length);\n        Buffer.BlockCopy(secondArray, 0, bytes, firstArray.Length, secondArray.Length);\n        return bytes;\n    }\n}\n\n// Usage example\npublic class Program\n{\n    public static void Main()\n    {\n        var hasher = new PasswordHasher();\n        \n        // Registration\n        string password = "fishlegs";\n        byte[] salt = hasher.GenerateSalt(16); // 128-bit salt\n        byte[] hash = hasher.ComputeHash(password, salt);\n        \n        // Store `hash` and `salt` in your database for the user 'barry'\n    }\n}