using System;\nusing System.Collections.Generic;\nusing System.Linq;\nusing System.Web.Http;\n\npublic class GridDataController : ApiController\n{\n    public IHttpActionResult GetData(int page = 1, int rows = 10)\n    {\n        // Sample data\n        var allRows = new List<GridRow>\n        {\n            new GridRow { Id = 1, Name = "John", LastName = "Doe" },\n            new GridRow { Id = 2, Name = "Jane", LastName = "Smith" },\n            // Add more rows as needed\n        };\n\n        int pageIndex = page - 1;\n        var result = allRows.Skip(pageIndex * rows).Take(rows);\n\n        return Ok(new\n        {\n            total = (int)Math.Ceiling(allRows.Count / (double)rows),\n            page,\n            records = allRows.Count,\n            rows = result.Select(r => new\n            {\n                id = r.Id,\n                cell = new[] { r.Name, r.LastName }\n            })\n        });\n    }\n}\n\npublic class GridRow\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n    public string LastName { get; set; }\n}