using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class HashingExample\n{\n    public static void Main()\n    {\n        string saltAndPwd = "yourSalt" + "yourPassword"; // Ensure this matches your concatenation logic\n        string hashedPwd = HashSHA1(saltAndPwd);\n        Console.WriteLine(hashedPwd);\n    }\n\n    public static string HashSHA1(string input)\n    {\n        using (SHA1 sha1 = SHA1.Create())\n        {\n            byte[] bytesToHash = Encoding.UTF8.GetBytes(input);\n            byte[] hashBytes = sha1.ComputeHash(bytesToHash);\n\n            // Convert byte array to hex format\n            StringBuilder sb = new StringBuilder();\n            foreach (byte b in hashBytes)\n            {\n                sb.Append(b.ToString("x2"));\n            }\n\n            return sb.ToString();\n        }\n    }\n}