using Microsoft.Reporting.WinForms;\n// ...\n\nprivate void ViewReport()\n{\n    ReportViewer reportViewer = new ReportViewer();\n    reportViewer.ProcessingMode = ProcessingMode.Remote;\n\n    // Set the report server URL and path\n    reportViewer.ServerReport.ReportServerUrl = new Uri("http://YourReportServer/ReportServer");\n    reportViewer.ServerReport.ReportPath = "/PathToYourReport";\n\n    // Add credentials if required\n    reportViewer.ServerReport.SetCredentials(new ReportParameter[] { }, \n                                           new DataSourceCredentials[] { },\n                                           new WindowsCredential());\n\n    // Refresh the report\n    reportViewer.RefreshReport();\n}