using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "your_connection_string_here";\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            try\n            {\n                connection.Open();\n                SqlCommand command = new SqlCommand("SELECT SUSER_SNAME(OWNER_SID) FROM sys.databases WHERE name = 'YourDatabaseName'", connection);\n                object owner = command.ExecuteScalar();\n                Console.WriteLine($"The owner of the database is: {owner}");\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"An error occurred: {ex.Message}");\n            }\n        }\n    }\n}