using System;\n   using YamlDotNet.Serialization;\n\n   namespace YamlExample\n   {\n       class Program\n       {\n           static void Main()\n           {\n               var deserializer = new DeserializerBuilder().Build();\n               \n               string input = @"\n               server: \n                 host: 192.68.0.1\n                 port: 8080";\n               \n               var config = deserializer.Deserialize<Config>(input);\n   \n               Console.WriteLine($"Server Host: {config.Server.Host}, Port: {config.Server.Port}");\n           }\n       }\n\n       public class Config\n       {\n           public Server Server { get; set; }\n       }\n\n       public class Server\n       {\n           public string Host { get; set; }\n           public int Port { get; set; }\n       }\n   }