protected void Application_BeginRequest(object sender, EventArgs e)\n{\n    // Check for IE6 in User Agent string\n    if (Request.UserAgent != null && Request.UserAgent.IndexOf("Mozilla/4.0", StringComparison.OrdinalIgnoreCase) >= 0 &&\n        Request.UserAgent.IndexOf("MSIE 6", StringComparison.OrdinalIgnoreCase) >= 0)\n    {\n        // Disable response compression\n        Response.Filter = new System.IO.StreamWriter(Response.OutputStream);\n        Response.AppendHeader("Content-encoding", "identity");\n    }\n}