using System;\nusing System.Runtime.InteropServices;\n\npublic class BrightnessController\n{\n    [DllImport("gdi32.dll", EntryPoint = "SetDeviceGammaRamp")]\n    public static extern bool SetDeviceGammaRamp(IntPtr hdc, ref RAMP lpRamp);\n\n    private struct RAMP\n    {\n        [MarshalAs(UnmanagedType.ByValArray, SizeConst = 256)]\n        public ushort Red;\n        [MarshalAs(UnmanagedType.ByValArray, SizeConst = 256)]\n        public ushort Green;\n        [MarshalAs(UnmanagedType.ByValArray, SizeConst = 256)]\n        public ushort Blue;\n    }\n\n    public static void SetBrightness(byte value)\n    {\n        IntPtr hdc = GetDC(IntPtr.Zero);\n        RAMP m_Ramp;\n        for (int i = 0; i < 256; i++)\n        {\n            short ideal = (short)((i * value) / 255);\n            m_Ramp.Red = ideal;\n            m_Ramp.Green = ideal;\n            m_Ramp.Blue = ideal;\n        }\n        SetDeviceGammaRamp(hdc, ref m_Ramp);\n        ReleaseDC(IntPtr.Zero, hdc);\n    }\n\n    [DllImport("user32.dll", EntryPoint = "GetDC")]\n    public static extern IntPtr GetDC(IntPtr hWnd);\n\n    [DllImport("user32.dll", EntryPoint = "ReleaseDC")]\n    public static extern int ReleaseDC(IntPtr hWnd, IntPtr hDc);\n\n    static void Main(string[] args)\n    {\n        byte brightnessLevel = 100; // from 0 (min) to 255 (max)\n        SetBrightness(brightnessLevel);\n        Console.WriteLine("Brightness set.");\n    }\n}