using System.ServiceModel;\n\nclass Program\n{\n    static void Main()\n    {\n        EndpointAddress endpoint = new EndpointAddress(\n            "net.pipe://localhost/phonestub");\n        ChannelFactory<IPhoneService> factory = \n            new ChannelFactory<IPhoneService>(new NetNamedPipeBinding(), endpoint);\n\n        IPhoneService channel = factory.CreateChannel();\n        channel.CallNumber("1234567890");\n\n        ((IClientChannel)channel).Close();\n        factory.Close();\n    }\n}