using System;\nusing System.Runtime.InteropServices;\n\npublic class TypeLibRegistration\n{\n    [DllImport("oleaut32.dll")]\n    private static extern void RegisterTypeLibraryForUser(IntPtr pTLB, string szFullPath, out IntPtr pTypelib);\n\n    public static void RegisterTypeLibUserFriendly(string typeLibPath)\n    {\n        IntPtr typelibrary = IntPtr.Zero;\n        try\n        {\n            // Load the type library from file path\n            IntPtr tlbHandle = Marshal.LoadTypeLib(typeLibPath);\n\n            // Register it for the current user\n            RegisterTypeLibraryForUser(tlbHandle, typeLibPath, out typelibrary);\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Failed to register type library: " + ex.Message);\n        }\n        finally\n        {\n            if (typelibrary != IntPtr.Zero)\n                Marshal.Release(typelibrary);\n        }\n    }\n}