using System;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        string fullProjectPath = "path_to_your_project.csproj";\n        XmlDocument projDefinition = new XmlDocument();\n        projDefinition.Load(fullProjectPath);\n\n        // Define the namespace manager with the default XML namespace used in csproj files\n        XmlNamespaceManager nsmgr = new XmlNamespaceManager(projDefinition.NameTable);\n        nsmgr.AddNamespace("msb", "http://schemas.microsoft.com/developer/msbuild/2003");\n\n        var nodeList = projDefinition.SelectNodes("//msb:Project/msb:ItemGroup/msb:Reference", nsmgr);\n\n        if (nodeList != null)\n        {\n            foreach (XmlNode node in nodeList)\n            {\n                Console.WriteLine(node.OuterXml);\n            }\n        }\n    }\n}