protected void ValidateInputs(object sender, EventArgs e)\n{\n    // Disable all validators initially\n    EmailValidator1.Enabled = false;\n    EmailValidator2.Enabled = false;\n\n    // Try validating each validator\n    if (!EmailValidator1.IsValid)\n    {\n        EmailValidator1.Enabled = true; // Enable only the first one that fails\n    }\n    else if (!EmailValidator2.IsValid)\n    {\n        EmailValidator2.Enabled = true; // Enable second validator if first is valid but second isn't\n    }\n\n    // Show error message if any validator fails\n    if (!Page.IsValid)\n    {\n        // Optionally, display a general error message in a Label or similar control\n        ErrorLabel.Text = "Please correct the errors in the form.";\n    }\n}