// Example of handling change tracking in a service method\n     public void UpdateUser(User updatedUser)\n     {\n         using (ISession session = sessionFactory.OpenSession())\n         {\n             using (ITransaction transaction = session.BeginTransaction())\n             {\n                 User user = session.Get<User>(updatedUser.UserId);\n                 if (user != null)\n                 {\n                     // Assuming you map properties manually or use some form of mapper\n                     CopyProperties(updatedUser, user);\n\n                     session.Update(user);\n                 }\n                 transaction.Commit();\n             }\n         }\n     }\n\n     private void CopyProperties(User source, User destination)\n     {\n         destination.Name = source.Name;\n         // Map other properties as needed\n     }