using System;\nusing System.Data;\nusing System.Globalization;\n\npublic void ConvertDateTimeColumnToString(DataTable dataTable)\n{\n    if (dataTable == null) return;\n\n    // Assuming the first column is of DateTime type; adjust the index or column name as necessary.\n    DataColumn dateColumn = dataTable.Columns["YourDateColumnName"];\n\n    foreach (DataRow row in dataTable.Rows)\n    {\n        if (row[dateColumn] != DBNull.Value && row[dateColumn] is DateTime dateTimeValue)\n        {\n            string formattedDate = dateTimeValue.ToString(CultureInfo.CurrentCulture.DateTimeFormat.ShortDatePattern);\n            row[dateColumn] = formattedDate;\n        }\n    }\n\n    // Now the specified column contains date strings in the desired format.\n}