using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\nclass Program {\n    static void Main(string[] args) {\n        List<int> numbers = new List<int> { 1, 2, 3, 4, 5, 6 };\n\n        // Using LINQBridge to filter numbers greater than 3\n        IEnumerable<int> filteredNumbers = from number in numbers\n                                          where number > 3\n                                          select number;\n\n        foreach (int number in filteredNumbers) {\n            Console.WriteLine(number);\n        }\n    }\n}