using com.tibco.tibems;\n\n   class Program\n   {\n       static void Main(string[] args)\n       {\n           ConnectionFactory factory = new TibjmsConnectionFactory("tcp://localhost:7222");\n           Connection connection = factory.CreateConnection();\n           try\n           {\n               connection.Start();\n               \n               // Create a session\n               Session session = connection.CreateSession(false, AcknowledgeMode.AUTO_ACKNOWLEDGE);\n\n               // Create a queue and producer\n               Queue queue = new TibjmsQueue("test_queue");\n               MessageProducer producer = session.CreateProducer(queue);\n\n               // Send a message\n               TextMessage textMessage = session.CreateTextMessage("Hello TIBCO EMS!");\n               producer.Send(textMessage);\n           }\n           finally\n           {\n               connection.Close();\n           }\n       }\n   }