using System;\nusing System.Data;\nusing Oracle.ManagedDataAccess.Client;\n\npublic int ExecuteUpdate(int[] ids, int value)\n{\n    int result = 0;\n\n    using (var connection = new OracleConnection("your_connection_string"))\n    {\n        var command = new OracleCommand("EXECUTE_UPDATE", connection);\n        command.CommandType = CommandType.StoredProcedure;\n\n        // Create and fill OracleStringArray\n        OracleParameter param1 = new OracleParameter();\n        param1.ParameterName = "ids";\n        param1.Value = new OracleDecimal[] { };\n        param1.CollectionType = OracleCollectionType.PLSQLAssociativeArray;\n        param1.ArrayBindSize = new int[ids.Length];\n        for (int i = 0; i < ids.Length; i++)\n        {\n            param1.ArrayBindSize[i] = sizeof(int);\n        }\n        OracleDecimal[] o_ids = Array.ConvertAll(ids, x => new OracleDecimal(x));\n        param1.Value = o_ids;\n        command.Parameters.Add(param1);\n\n        OracleParameter param2 = new OracleParameter("value", value);\n        command.Parameters.Add(param2);\n\n        OracleParameter resultParam = new OracleParameter();\n        resultParam.Direction = ParameterDirection.ReturnValue;\n        command.Parameters.Add(resultParam);\n\n        connection.Open();\n        command.ExecuteNonQuery();\n\n        // Retrieve the return value\n        result = Convert.ToInt32(resultParam.Value);\n    }\n\n    return result;\n}