using Microsoft.EntityFrameworkCore;\nusing System.Linq;\n\npublic class SalesContext : DbContext\n{\n    public DbSet<Sale> Sales { get; set; }\n}\n\npublic class Sale\n{\n    public int Id { get; set; }\n    public decimal TotalSales { get; set; } // Assuming this is the column name\n}\n\n// Usage in code\nusing (var context = new SalesContext())\n{\n    var total = context.Sales.Sum(s => s.TotalSales); // This will be checked at compile time if properties are correct\n}