using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        PerformanceCounter cacheCounter = new PerformanceCounter("ASP.NET Applications", "Cache Total Memory Usage", "_Total");\n\n        try\n        {\n            // Get the current value of the Cache Total Memory Usage counter\n            float cacheMemoryUsage = cacheCounter.NextValue();\n\n            Console.WriteLine($"Current ASP.NET Cache Memory Usage: {cacheMemoryUsage} bytes");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error accessing performance counter: " + ex.Message);\n        }\n    }\n}