using System;\n\npublic class Program\n{\n    public static void Main()\n    {\n        IsCalledFromService();\n    }\n\n    public static void IsCalledFromService()\n    {\n        if (Environment.UserInteractive)\n        {\n            Console.WriteLine("Method is called from an interactive application.");\n        }\n        else\n        {\n            Console.WriteLine("Method is called from a Windows Service.");\n        }\n    }\n}