using System;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string wikiText = "==Heading==\nThis is --bold-- text.";\n        \n        string htmlText = ConvertWikiToHtml(wikiText);\n        Console.WriteLine(htmlText);\n    }\n\n    static string ConvertWikiToHtml(string wikiText)\n    {\n        // Replace ==Heading== with <h2>Heading</h2>\n        string result = Regex.Replace(wikiText, @"==([^=]+)==", "<h2>$1</h2>");\n        \n        // Replace --bold-- with <b>bold</b>\n        result = Regex.Replace(result, "--(.*?)--", "<b>$1</b>");\n        \n        return result;\n    }\n}