// When creating a new record:\npublic ActionResult Create(AccountViewModel model)\n{\n    var account = new Account { \n        // Populate other properties...\n        NonSensitiveId = Guid.NewGuid().ToString(),\n        SensitiveData = model.SensitiveData\n    };\n    db.Accounts.Add(account);\n    db.SaveChanges();\n    \n    return RedirectToAction("Details", new { id = account.NonSensitiveId });\n}\n\n// In the view for listing accounts:\n@foreach (var account in Model) {\n    <a href="@Url.Action("Details", "Accounts", new { id = account.NonSensitiveId })">@account.Name</a>\n}\n\n// When accessing an account by its non-sensitive ID:\npublic ActionResult Details(string id)\n{\n    var account = db.Accounts.FirstOrDefault(a => a.NonSensitiveId == id);\n    if (account == null) return HttpNotFound();\n\n    var viewModel = new AccountViewModel\n    {\n        Name = account.Name,\n        // Populate other properties...\n        NonSensitiveId = account.NonSensitiveId,\n        SensitiveData = account.SensitiveData  // Use cautiously and ensure the action method is protected\n    };\n    \n    return View(viewModel);\n}\n\npublic class Account\n{\n    public int Id { get; set; }\n    public string NonSensitiveId { get; set; }  // Add this field for non-sensitive identifier\n    public string SensitiveData { get; set; }   // Existing sensitive data field\n    public string Name { get; set; }            // Example other property\n}\n\npublic class AccountViewModel\n{\n    public int Id { get; set; }\n    public string NonSensitiveId { get; set; }\n    public string SensitiveData { get; set; }\n    public string Name { get; set; }\n}