@using (Html.BeginForm())\n{\n    <label for="Countries">Country:</label>\n    @Html.DropDownList("Countries", new SelectList(new List<SelectListItem>(), "Value", "Text"), "--Select Country--", new { id = "ddlCountries" })\n    \n    <label for="Cities">City:</label>\n    @Html.DropDownList("Cities", new SelectList(new List<SelectListItem>(), "Value", "Text"), "--Select City--", new { id = "ddlCities" })\n\n    @section Scripts {\n        <script type="text/javascript">\n            $(function() {\n                $('#ddlCountries').change(function () {\n                    $.getJSON('@Url.Action("GetCitiesByCountryId")', { countryId: $(this).val() }, function (data) {\n                        var items = '';\n                        $('#ddlCities').empty();\n                        $.each(data, function (i, city) {\n                            items += "<option value='" + city.Id + "'>" + city.Name + "</option>";\n                        });\n                        $('#ddlCities').html(items);\n                    });\n                });\n            });\n\n            // Load initial countries\n            $(document).ready(function () {\n                $.getJSON('@Url.Action("GetCountries")', function (data) {\n                    var items = '';\n                    $.each(data, function (i, country) {\n                        items += "<option value='" + country.Id + "'>" + country.Name + "</option>";\n                    });\n                    $('#ddlCountries').html(items);\n                });\n            });\n        </script>\n    }\n}