@using (Html.BeginForm("MyAction", "Home", FormMethod.Post))\n{\n    @Html.AntiForgeryToken() <!-- This will generate the anti-forgery token -->\n    \n    <input type="hidden" id="myData" value="someValue" />\n    <button id="submit">Submit</button>\n}\n\n<script src="https://ajax.googleapis.com/ajax/libs/jquery/3.5.1/jquery.min.js"></script>\n<script>\n    $(document).ready(function () {\n        $('#submit').click(function() {\n            $.ajax({\n                type: 'POST',\n                url: '@Url.Action("MyAction", "Home")',\n                data: { \n                    __RequestVerificationToken: $('input[name="__RequestVerificationToken"]').val(), // Include antiforgery token\n                    myData: $('#myData').val()\n                },\n                success: function(result) {\n                    // Handle success\n                }\n            });\n        });\n    });\n</script>