@{\n    ViewBag.Title = "Live Search";\n}\n\n<h2>Live Search</h2>\n<input type="text" id="searchBox" placeholder="Type to search..." />\n<ul id="searchResults"></ul>\n\n@section Scripts {\n    <script src="https://code.jquery.com/jquery-3.6.0.min.js"></script>\n    <script>\n        $(document).ready(function () {\n            $('#searchBox').keyup(function (e) {\n                var query = $(this).val();\n                \n                if (query.length > 2) { // Start searching after at least 3 characters\n                    $.ajax({\n                        url: '@Url.Action("LiveSearch", "Search")',\n                        type: 'GET',\n                        data: { query: query },\n                        success: function (data) {\n                            $('#searchResults').empty();\n                            \n                            if (data.length > 0) {\n                                $.each(data, function (index, item) {\n                                    $('#searchResults').append($('<li>').text(item));\n                                });\n                            } else {\n                                $('#searchResults').append($('<li>').text('No results found'));\n                            }\n                        },\n                        error: function () {\n                            $('#searchResults').empty().append($('<li>').text('An error occurred'));\n                        }\n                    });\n                } else {\n                    $('#searchResults').empty();\n                }\n            });\n        });\n    </script>\n}