@model IEnumerable<dynamic>\n\n@foreach (var group in Model)\n{\n    <h3>@group.GroupName</h3>\n    <table border="1">\n        <tr>\n            <th>FileID</th>\n            <th>FileName</th>\n            <th>FileSize</th>\n        </tr>\n        @foreach (var file in group.Files)\n        {\n            <tr>\n                <td>@file.FileID</td>\n                <td>@file.FileName</td>\n                <td>@file.FileSize</td>\n            </tr>\n        }\n    </table>\n    <hr />\n}