public class Camera\n{\n    public Vector3 Position { get; private set; }\n    public Vector3 Target { get; private set; }\n    public Vector3 UpVector { get; private set; }\n\n    public Matrix View { get; private set; }\n    public Matrix Projection { get; private set; }\n\n    publicCamera(GraphicsDevice graphics)\n    {\n        Position = new Vector3(0, 5, -10);\n        Target = Vector3.Zero;\n        UpVector = Vector3.UnitY;\n\n        CreateViewMatrix();\n        CreateProjectionMatrix(graphics.Viewport.Width, graphics.Viewport.Height);\n    }\n\n    private void CreateViewMatrix()\n    {\n        View = Matrix.CreateLookAt(Position, Target, UpVector);\n    }\n\n    public void CreateProjectionMatrix(int screenWidth, int screenHeight)\n    {\n        float aspectRatio = (float)screenWidth / screenHeight;\n        float fieldOfView = MathHelper.ToRadians(45.0f); // 45 degrees\n        float nearClipPlane = 0.1f;\n        float farClipPlane = 2000f;\n\n        Projection = Matrix.CreatePerspectiveFieldOfView(fieldOfView, aspectRatio, nearClipPlane, farClipPlane);\n    }\n}