using InTheHand.Net.Sockets;\nusing System;\n\nprivate void SetupBluetoothClient()\n{\n    BluetoothClient client = new BluetoothClient();\n    // Assuming serverAddress is already known and trusted.\n    var remoteEndPoint = new BluetoothDeviceInfo(serverAddress).EndPoints[0];\n\n    // Set desired security:\n    client.Authentication = true; // Request authentication\n    client.Encrypt = true;       // Force encryption\n\n    try {\n        // Connect to the remote device, expecting secure connection\n        client.Connect(remoteEndPoint);\n        \n        var stream = client.GetStream();\n        // Use stream for sending/receiving data securely.\n    }\n    catch (Exception ex)\n    {\n        Console.WriteLine("Failed to connect or establish a secure channel: " + ex.Message);\n    }\n}