using System.Data;\nusing System.Data.OracleClient;\n\npublic void SetSchema(string connectionStr, string schemaName)\n{\n    using (var conn = new OracleConnection(connectionStr))\n    {\n        try\n        {\n            conn.Open();\n            var cmd = new OracleCommand($"ALTER SESSION SET CURRENT_SCHEMA={schemaName};", conn);\n            cmd.ExecuteNonQuery();\n\n            // Execute your other queries here with the session setting applied.\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error setting schema: " + ex.Message);\n        }\n    }\n}