using System;\nusing System.Drawing;\n\npublic static class ColorExtensions\n{\n    public static string GetTintHexString(this Color color, double tintPercentage)\n    {\n        // Ensure the tint percentage is within 0-100\n        if (tintPercentage < 0 || tintPercentage > 100)\n            throw new ArgumentOutOfRangeException(nameof(tintPercentage), "Tint percentage must be between 0 and 100.");\n\n        // Calculate the factor for interpolation\n        double factor = tintPercentage / 100.0;\n\n        // Interpolate each color channel with white (255, 255, 255)\n        int r = (int)((255 - color.R) * factor + color.R);\n        int g = (int)((255 - color.G) * factor + color.G);\n        int b = (int)((255 - color.B) * factor + color.B);\n\n        // Create a new Color object\n        Color tintedColor = Color.FromArgb(r, g, b);\n\n        // Convert the Color to HEX HTML string format\n        return $"#{tintedColor.R:X2}{tintedColor.G:X2}{tintedColor.B:X2}";\n    }\n}\n\n// Usage example:\nvoid Main()\n{\n    Color originalColor = Color.FromName("blue");\n    double tintPercentage = 90;\n    string tintedHex = originalColor.GetTintHexString(tintPercentage);\n    Console.WriteLine($"Original Color: {originalColor.Name}, Tinted ({tintPercentage}%): {tintedHex}");\n}