using System;\nusing System.Data.SqlClient;\n\npublic class SurveyDataAccess\n{\n    private string connectionString = "your_connection_string_here";\n\n    public void InsertSurveyResponse(int optionId, string comments)\n    {\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            string query = "INSERT INTO SurveyResponses (OptionID, Comments) VALUES (@optionId, @comments)";\n            \n            using (SqlCommand command = new SqlCommand(query, connection))\n            {\n                // Add parameters to the SQL command\n                command.Parameters.AddWithValue("@optionId", optionId);\n                command.Parameters.AddWithValue("@comments", comments);\n\n                try\n                {\n                    connection.Open();\n                    command.ExecuteNonQuery();\n                }\n                catch (Exception ex)\n                {\n                    // Log the error here, do not show detailed error messages to users\n                    Console.WriteLine("Error: " + ex.Message);\n                }\n            }\n        }\n    }\n}