using System;\nusing System.Data;\n\nclass Program\n{\n    static void Main()\n    {\n        // Assume 'dataTable' is your DataTable object populated with data from the query.\n        if (dataTable != null && dataTable.Rows.Count > 0)\n        {\n            // Accessing the first row of the DataTable\n            DataRow row = dataTable.Rows[0];\n\n            // Replace "ColumnName" with the actual column name you want to access\n            object value = row["ColumnName"];\n\n            // If you need the value as a specific type, you can cast it.\n            // For example, if the value is an integer:\n            int intValue = (int)value;\n\n            Console.WriteLine("The value from the DataTable is: " + value);\n        }\n        else\n        {\n            Console.WriteLine("DataTable is empty or null.");\n        }\n    }\n}