protected void Page_Load(object sender, EventArgs e)\n{\n  if (IsPostBack)\n  {\n    string dateRange = Request.Form["datepicker"];\n    DateTime startDate, endDate;\n\n    if (TryParseDateRange(dateRange, out startDate, out endDate))\n    {\n      // Process dates, ensure they are valid\n      if (startDate <= endDate)\n      {\n        // Dates are valid, proceed with your logic\n      }\n      else\n      {\n        lblError.Text = "Start date must be before or equal to End date.";\n      }\n    }\n    else\n    {\n      lblError.Text = "Invalid date range format.";\n    }\n  }\n}\n\nprivate bool TryParseDateRange(string dateRange, out DateTime startDate, out DateTime endDate)\n{\n  var parts = dateRange.Split(new[] { '-' }, StringSplitOptions.RemoveEmptyEntries);\n  startDate = new DateTime();\n  endDate = new DateTime();\n\n  if (parts.Length == 2 && DateTime.TryParseExact(parts[0].Trim(), "M/d/yyyy", CultureInfo.InvariantCulture, DateTimeStyles.None, out startDate) \n      && DateTime.TryParseExact(parts[1].Trim(), "M/d/yyyy", CultureInfo.InvariantCulture, DateTimeStyles.None, out endDate))\n  {\n    return true;\n  }\n\n  return false;\n}