using System;\nusing System.Collections.Generic;\nusing System.Data;\nusing System.Windows.Controls;\n\n// Assuming 'dataSet' is your filled DataSet object.\nDataSet dataSet = ...;\n\n// Get the specific DataTable by name or index.\nDataTable dataTable = dataSet.Tables["YourTableName"]; // Replace with your table's name.\n\n// Extract a single column, e.g., "ColumnName".\nvar items = new List<string>();\nforeach (DataRow row in dataTable.Rows)\n{\n    if (!Convert.IsDBNull(row["ColumnName"])) // Prevent adding null values\n    {\n        items.Add(row["ColumnName"].ToString());\n    }\n}\n\n// Bind the list to ComboBox's ItemSource.\nComboBox comboBox = ...; // Your ComboBox instance.\ncomboBox.ItemsSource = items;\n\n// Optionally set DisplayMemberPath and SelectedValuePath if needed.\ncomboBox.DisplayMemberPath = "YourDisplayMember"; // Adjust as necessary.\ncomboBox.SelectedValuePath = "YourSelectedValueMember"; // Adjust as necessary.