using System;\nusing System.Runtime.InteropServices;\n\npublic class ComComponentChecker\n{\n    public static bool IsComComponentInstalled(Guid clsid)\n    {\n        try\n        {\n            Type type = Type.GetTypeFromCLSID(clsid);\n            return type != null;\n        }\n        catch (COMException)\n        {\n            // Log if necessary, but typically no COMException is thrown for unregistered components.\n            return false;\n        }\n    }\n\n    public static bool IsComComponentInstalled(string progId)\n    {\n        try\n        {\n            Type type = Type.GetTypeFromProgID(progId);\n            return type != null;\n        }\n        catch (COMException ex)\n        {\n            // Log ex if necessary, but usually not needed for unregistered components.\n            return false;\n        }\n    }\n}