using System;\nusing System.Text;\n\npublic class DataTableExample\n{\n    public static void Main()\n    {\n        // Example data table\n        var dataTable = new string[] { "Item1", "Item2", "Item3", "Item4", "Item5", \n                                       "Item6", "Item7", "Item8", "Item9", "Item10",\n                                       "Item11", "Item12", "Item13", "Item14", "Item15", \n                                       "Item16", "Item17", "Item18", "Item19", "Item20" };\n        int numberOfItemsPerRow = 5;\n\n        // StringBuilder to build the HTML\n        var htmlBuilder = new StringBuilder();\n        htmlBuilder.Append("<table border='1'>");\n\n        for (int i = 0; i < dataTable.Length;)\n        {\n            htmlBuilder.Append("<tr>");\n\n            for (int j = 0; j < numberOfItemsPerRow && i < dataTable.Length; j++, i++)\n            {\n                htmlBuilder.AppendFormat("<td>{0}</td>", dataTable[i]);\n            }\n\n            htmlBuilder.Append("</tr>");\n        }\n\n        htmlBuilder.Append("</table>");\n\n        // Output the HTML\n        Console.WriteLine(htmlBuilder.ToString());\n    }\n}