// Example of setting up Entity Framework Core for Oracle and SQL Server\nusing Microsoft.EntityFrameworkCore;\n\npublic class MyDbContext : DbContext\n{\n    public DbSet<MyTable> MyTables { get; set; }\n\n    protected override void OnConfiguring(DbContextOptionsBuilder optionsBuilder)\n    {\n        // Configure for SQL Server\n        optionsBuilder.UseSqlServer("Your SQL Server connection string");\n\n        // Comment out the above and uncomment below to switch to Oracle\n        //.optionsBuilder.UseOracle("Your Oracle connection string");\n    }\n}