using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\nclass Program\n{\n    static void Main()\n    {\n        string secretkey = "s934kladfklada"; // Assuming this is the prefix you are using\n        string email = "@a.com";\n        string encode = secretkey + email;\n\n        // Use UTF-8 encoding instead of Unicode\n        Encoding encoder = new UTF8Encoding();\n        byte[] MessageBytes = encoder.GetBytes(encode);\n\n        SHA1Managed SHhash = new SHA1Managed();\n        byte[] HashValue = SHhash.ComputeHash(MessageBytes);\n\n        StringBuilder strHex = new StringBuilder();\n        foreach (byte b in HashValue)\n        {\n            strHex.Append(String.Format("{0:x2}", b));\n        }\n\n        Console.WriteLine(strHex.ToString()); // This should now match the PHP output\n    }\n}