using System;\n   using System.Data.SQLite;\n\n   class Program\n   {\n       static void Main()\n       {\n           string connectionString = "Data Source=example.db3;Version=3;";\n           \n           // Create a table (only once)\n           CreateTable(connectionString);\n\n           // Insert data\n           InsertData(connectionString);\n\n           // Read data\n           ReadData(connectionString);\n       }\n\n       private static void CreateTable(string connectionString)\n       {\n           using (var connection = new SQLiteConnection(connectionString))\n           {\n               connection.Open();\n               string sql = "CREATE TABLE IF NOT EXISTS People (Id INT, Name VARCHAR(20))";\n               using (var command = new SQLiteCommand(sql, connection))\n               {\n                   command.ExecuteNonQuery();\n               }\n           }\n       }\n\n       private static void InsertData(string connectionString)\n       {\n           using (var connection = new SQLiteConnection(connectionString))\n           {\n               connection.Open();\n               string sql = "INSERT INTO People (Id, Name) VALUES (1, 'John Doe')";\n               using (var command = new SQLiteCommand(sql, connection))\n               {\n                   command.ExecuteNonQuery();\n               }\n           }\n       }\n\n       private static void ReadData(string connectionString)\n       {\n           using (var connection = new SQLiteConnection(connectionString))\n           {\n               connection.Open();\n               string sql = "SELECT Id, Name FROM People";\n               using (var command = new SQLiteCommand(sql, connection))\n               {\n                   using (var reader = command.ExecuteReader())\n                   {\n                       while (reader.Read())\n                       {\n                           Console.WriteLine($"Id: {reader["Id"]}, Name: {reader["Name"]}");\n                       }\n                   }\n               }\n           }\n       }\n   }