using System;\nusing Microsoft.Web.Administration;\n\npublic class IISEditor\n{\n    public void ChangeVirtualDirectoryPath(string siteName, string virtualDirPath, string newPhysicalPath)\n    {\n        using (ServerManager serverManager = new ServerManager())\n        {\n            Site site = serverManager.Sites[siteName];\n            if (site == null) throw new ArgumentException("Site not found.");\n\n            VirtualDirectory vdir = site.Applications["/"].VirtualDirectories[virtualDirPath];\n            if (vdir == null) throw new ArgumentException("Virtual directory not found.");\n\n            vdir.PhysicalPath = newPhysicalPath;\n            \n            serverManager.CommitChanges();\n        }\n    }\n\n    public static void Main(string[] args)\n    {\n        IISEditor editor = new IISEditor();\n\n        // Example to change the Virtual Directory path.\n        editor.ChangeVirtualDirectoryPath(\n            "Default Web Site", \n            "/app", \n            @"C:\Projects\....\branches\Prod\");\n        \n        Console.WriteLine("Virtual directory path updated successfully.");\n    }\n}