using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\nclass Bloop\n{\n    public string FirstName { get; set; }\n    public string LastName { get; set; }\n    public string Address { get; set; }\n    public string Number { get; set; }\n    public string OtherStuff { get; set; }\n}\n\nclass Razzie\n{\n    public string FirstName { get; set; }\n    public string LastName { get; set; }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        List<Bloop> bloops = new List<Bloop>\n        {\n            new Bloop { FirstName = "John", LastName = "Doe" },\n            new Bloop { FirstName = "Jane", LastName = "Smith" }\n        };\n\n        var razzies = bloops.Select(b => new Razzie\n        {\n            FirstName = b.FirstName,\n            LastName = b.LastName\n        }).ToList();\n\n        foreach (var razzie in razzies)\n        {\n            Console.WriteLine($"{razzie.FirstName} {razzie.LastName}");\n        }\n    }\n}