using Prism.Events;\nusing System.ComponentModel;\n\nnamespace TestMvvm444.ViewModel\n{\n    public class CustomerViewModel : INotifyPropertyChanged, IDataErrorInfo\n    {\n        private readonly IEventAggregator _eventAggregator;\n        private Customer _customer;\n\n        public CustomerViewModel(Customer customer, IEventAggregator eventAggregator)\n        {\n            _customer = customer;\n            _customer.PropertyChanged += Customer_PropertyChanged;\n            _eventAggregator = eventAggregator;\n        }\n\n        // Properties\n        public string FirstName\n        {\n            get { return _customer.FirstName; }\n            set { _customer.FirstName = value; }\n        }\n\n        public string LastName\n        {\n            get { return _customer.LastName; }\n            set { _customer.LastName = value; }\n        }\n\n        private void Customer_PropertyChanged(object sender, PropertyChangedEventArgs e)\n        {\n            OnPropertyChanged(e.PropertyName);\n            if (_customer.HasErrors)\n                _eventAggregator.GetEvent<ErrorNotificationEvent>().Publish(_customer.GetErrors(e.PropertyName));\n        }\n\n        public string FullName\n        {\n            get { return _customer.FullName; }\n        }\n\n        // Errors...\n    }\n}