using OpenQA.Selenium;\nusing OpenQA.Selenium.Chrome;\n\nclass Program\n{\n    static void Main()\n    {\n        // Setup Chrome options if needed\n        var chromeOptions = new ChromeOptions();\n        \n        // Initialize the ChromeDriver\n        IWebDriver driver = new ChromeDriver(chromeOptions);\n\n        try\n        {\n            // Navigate to the webpage\n            driver.Navigate().GoToUrl("https://example.com");\n\n            // Find the textbox by its ID, name, or other attributes and send text\n            IWebElement textBox = driver.FindElement(By.Id("textboxId"));\n            textBox.SendKeys("Hello, world!");\n\n            // Optionally, submit a form after entering the text\n            IWebElement submitButton = driver.FindElement(By.Id("submitButtonId"));\n            submitButton.Click();\n        }\n        finally\n        {\n            // Close the browser window and quit the driver\n            driver.Quit();\n        }\n    }\n}