using iTextSharp.text;\n  using iTextSharp.text.html.simpleparser;\n  using System.IO;\n\n  public byte[] GeneratePdfFromHtml(string htmlContent)\n  {\n      var bytes = new byte[0];\n      var msOutput = new MemoryStream();\n      var document = new Document(PageSize.A4, 25.0F, 25.0F, 30.0F, 30.0F);\n      using (var writer = PdfWriter.GetInstance(document, msOutput))\n      {\n          document.Open();\n          using (var reader = new StringReader(htmlContent))\n          {\n              var htmlWorker = HtmlWorker.ParseToList(reader, null);\n              foreach (IElement element in htmlWorker)\n                  document.Add(element);\n          }\n          document.Close();\n          bytes = msOutput.ToArray();\n      }\n\n      return bytes;\n  }