protected string FormatDate(object dateObject)\n{\n    DateTime specificDate = new DateTime(2023, 1, 1); // The date you want to check against\n    if (dateObject != null && DateTime.TryParse(dateObject.ToString(), out DateTime cellDate) && cellDate == specificDate)\n    {\n        return string.Empty; // Return an empty string for this specific date\n    }\n    return dateObject.ToString(); // Otherwise, return the formatted date\n}