using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        List<int> list1 = new List<int> { 1, 2, 3 };\n        List<string> list2 = new List<string> { "a", "b", "c" };\n\n        var zipped = list1.Zip(list2, (v1, v2) => $"{v1} {v2}");\n\n        foreach (var item in zipped)\n        {\n            Console.WriteLine(item);\n        }\n    }\n}