using System;\nusing System.Collections.Generic;\nusing System.Data.SqlClient;\n\npublic class OrderService\n{\n    private string connectionString = "your_connection_string_here";\n\n    public List<Order> GetDetailedOrders(List<string> orderNumbers)\n    {\n        var orders = new List<Order>();\n\n        using (var connection = new SqlConnection(connectionString))\n        {\n            string query = @"\n                SELECT OrderNumber, CustomerName, TotalAmount, OrderDate\n                FROM Orders\n                WHERE OrderNumber IN (@OrderNumbers)";\n\n            // Convert list to comma-separated string for SQL 'IN' clause\n            var orderNumberList = string.Join(",", orderNumbers);\n\n            using (var command = new SqlCommand(query, connection))\n            {\n                command.Parameters.AddWithValue("@OrderNumbers", orderNumberList);\n\n                connection.Open();\n                using (var reader = command.ExecuteReader())\n                {\n                    while (reader.Read())\n                    {\n                        orders.Add(new Order\n                        {\n                            OrderNumber = reader["OrderNumber"].ToString(),\n                            CustomerName = reader["CustomerName"].ToString(),\n                            TotalAmount = Convert.ToDecimal(reader["TotalAmount"]),\n                            OrderDate = Convert.ToDateTime(reader["OrderDate"])\n                        });\n                    }\n                }\n            }\n        }\n\n        return orders;\n    }\n}\n\npublic class Order\n{\n    public string OrderNumber { get; set; }\n    public string CustomerName { get; set; }\n    public decimal TotalAmount { get; set; }\n    public DateTime OrderDate { get; set; }\n}