using System;\n\npublic class GuidExample {\n    public static void Main() {\n        // Generate a new GUID for demonstration purposes.\n        Guid guid = Guid.NewGuid();\n        Console.WriteLine("Generated GUID: " + guid);\n        \n        // Example in a multithreaded context\n        var t1 = Task.Run(() => Console.WriteLine("Task 1 GUID: " + Guid.NewGuid()));\n        var t2 = Task.Run(() => Console.WriteLine("Task 2 GUID: " + Guid.NewGuid()));\n\n        Task.WaitAll(t1, t2);\n    }\n}